
public class Test05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//...
		
		int aaa1 = 3;
		//메모리 값을 1을 증가 시키고 싶다.
	/*1*/	aaa1 = aaa1 + 1; // 2이상의 값을 증가시키고 싶을 때 쓰임.
	/*2*/	aaa1 =+ 1; // write 단축 배정 연산자 
					// 2이상의 값을 증가시키고 싶을 때 쓰임.(일반적으로 선호됨.)
	/*3*/	aaa1++; // write - 메모리 값이 변함 -> 1증가시킬때는 주로쓰임
	
			aaa1--; // 1이 감소됨.
			
	
		
	}

}
