
public class Test06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 증감연산자 = , --
		
		int aaa1 = 3;
		System.out.println(aaa1);
		
		aaa1++;
		System.out.println(aaa1);
		
		aaa1++;
		System.out.println(aaa1);
		
		aaa1--;
		System.out.println(aaa1);
		
		++aaa1;
		System.out.println(aaa1);
		
		--aaa1;
		System.out.println(aaa1);
		
		
		

	}

}
