
public class Test08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 단축배정 연산자... 코드의 간결성을 위한 
		int aaa1 = 0;
		
		aaa1 = aaa1 + 3;
		aaa1 += 3;
		System.out.println(aaa1);
		
		aaa1 -= 2;
		System.out.println(aaa1);
		
		aaa1 *= 2; // 곱하기 나누기 나머지는 잘 쓰이지 않음.
		System.out.println(aaa1);
		
		aaa1 /= 2;
		System.out.println(aaa1);
		
		aaa1 %= 3;
		System.out.println(aaa1);
		

		
		
	}

}
