
public class Test15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 비교연산자 .... 크냐 작냐 같냐 등등... 대부분 if 문에서 쓰임
		
		int a = 7;
		
		boolean test = a > 10;//초과 false
		test = a >= 10; //이상		false
		test = a < 10; // 미만		true
		test = a <= 7; // 이하		true
		
		test = a == 10; //같냐? 		false
		test = a != 10; //다르냐?
		
		System.out.println(test);
		
		
		//if(a < 10) {
		//if의 () 안에는 true or false = boolean 타입만 들어갈수 있음 - 자바기준	
			
			
			
		

	}

}
